import React , {useEffect , useState } from 'react';
import { useSelector } from 'react-redux';
import './addactivities';
import { postActivity } from '../../Actions/actions';


export default function  AddActivity() {
  const activities = useSelector(state => state.activities)
  const [input , setInput] = useState({
      name: '',
      dificulty: 1,
      duration:'',
      season:'',
      countries:[]
    })


   const handleInputChange = function(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  }

  return (

    <form>
      <h1>Agregar Actividad Turistica</h1>
        <label>Nombre actividad</label>
        <input className='dato' type="text" name='name' value={input.name} onChange={(e) => handleInputChange(e)}/>
        <label>Dificultad</label>
        <input className='dato' type="range" name="dificulty"  min="1" max="5" step="1" value={input.username}
         onChange={(e) => handleInputChange(e)}/>
        <label>Duracion</label>
        <input className='dato' type="range" value={input.username}onChange={(e) => handleInputChange(e)}/>
       <div className='input'>
    <h3>Temporada</h3>
        <select className='select' type='check' name="season">
    <option>Otono</option>
    <option>Invierno</option>
    <option>Primavera</option>
    <option>Verano</option>
         </select>
       </div> 

      <input className='button' type="submit" />
    </form>
  )
}


// Ruta de creación de actividad turística: debe contener

