import React , {useEffect , useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './addactivities';
import axios from 'axios';
import { getAllCountries } from '../../Actions/actions';
import { connect } from 'react-redux';

export function  AddActivity(props) {
  const activities = useSelector(state => state.activities)
  const paises = useSelector(state => state.allcountries); 
  const [input , setInput] = useState({
      name: '',
      dificulty: 1,
      duration:1,
      season:'',
      idcountries:[]
    })
  let idcountries = [];
  const dispatch = useDispatch();



   const handleInputChange = function(e) {
    e.preventDefault();
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  }

  const submit = function () {
    let { name , dificulty , duration , season , idcountries } = input
    axios.post('http://localhost:3001/activity',
    { name, dificulty, duration , season , idcountries })
    .then(r => console.log(r))
  }

  let countries = [];
  function addCountry() {
    countries = countries + countries.push(input.idcountries)
    console.log(countries)
     }

  return (
    <div className='form'>
    <div >
      <div>
      <h1>Agregar Actividad Turistica</h1>
      <hr />
      </div>
        <div className='inputs'>
        <label>Nombre actividad</label>
        <input className='dato' type="text" name='name' value={input.name} onChange={(e) => handleInputChange(e)}/>
        <label>Dificultad</label>
        <input className='dato' type="range" name="dificulty"  min="1" max="5" step="1" value={input.dificulty}
         onChange={(e) => handleInputChange(e)}/>
        <label>Duracion</label>
        <input className='dato' type='number' name='duration' min='1' max='24' value={input.duration} onChange={(e) => handleInputChange(e)}/>
        <label>Paises en los que se puede realizar</label>       
        <input className='dato' type="text" name='countries' value={input.countries}  onChange={(e) => handleInputChange(e)}/>
        </div>
        <label>Temporada</label> 
        <select name='season' value={input.season} onChange={(e) => handleInputChange(e)}>
          <option  value='otono'>Otoño</option>
          <option  value='invierno'>Invierno</option>
          <option  value='primavera'>Primavera</option>
          <option  value='verano'>Verano</option>
        </select >
        <span className='labels'>Selecciona los paises a asignarle la actividad </span>
				<select name='idcountries' value={input.idcountries} onChange={handleInputChange} defaultValue='DEFAULT' >
					<option name='DEFAULT' disabled>                                   
						Paises...                         
					</option>
					{
						paises &&                   
							paises.map( pais => (								
								<option name={input.countries} key={pais.idCountry} value={pais.idCountry}>                                   
									{pais.name}                            
								</option>								        
							))
					}
				</select>  <button onClick={addCountry}>Agregar Pais</button>
        <span>{input.countries && 
                 input.countries.map( pais => (<p key={pais.name}>{pais.name}</p>))} </span>
      <input className='button' type="submit"  onClick={submit}/>
    </div>
    </div>
  )
 }



const mapStateToProps = state => {
  return {
    paises : state.allcountries
  }
}

export default connect(mapStateToProps, null)(AddActivity)

// Ruta de creación de actividad turística: debe contener

// [x] Un formulario controlado con los siguientes campos
// Nombre
// Dificultad
// Duración
// Temporada
// [ ] Posibilidad de seleccionar/agregar varios países en simultaneo
// [x] Botón/Opción para crear una nueva actividad turística
