import React, { useEffect , useState} from 'react'
import { connect } from 'react-redux'
import { useSelector , useDispatch } from 'react-redux';
import { getAllActivities } from '../../Actions/actions';

export const Activities = () => {
    const activities = useSelector(state => state.activities)
    const dispatch = useDispatch()
    const [estado, setEstado] = useState({
        countries : [],
        activity:{},
        activities: 0,
    })

useEffect(() => {
   dispatch(getAllActivities());
}, [dispatch])

const handleInputChange = function(e) {
    e.preventDefault();
    setEstado({
      ...estado,
      [e.target.name]: e.target.value
    });
  }



console.log(activities)

    return (
        <div>
             <select name='activities' value={activities} onChange={handleInputChange} defaultValue='DEFAULT' >
					<option name='DEFAULT' >                                   
						Buscar por Actividad...                       
					</option>
          {
						activities &&                   
							activities.map( act => (								
								<option name={estado.countries} key={act.id} value={act.id}>                                   
									{act.name}                            
								</option>								        
							))
					}
				</select> 
        </div>
    )
}


const mapStateToProps = (state) => ({
    activities : state.activities
})

