import { useSelector } from 'react-redux';
import './addactivities';
import { postActivity } from '../../Actions/actions';


export default function  AddActivity() {
  const activities = useSelector(state => state.activities)
  const [input , setInput] = useState({
      name: '',
      dificulty: 1,
      duration:1,
      season:'',
      countries:[]
    })


   const handleInputChange = function(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  }

  const submit = function () {

  }

  return (
    <div className='form'>
    <form >
      <div>

      <hr />
      </div>
        <div className='inputs'>
        <label>Nombre actividad</label>
        <input className='dato' type="text" name='name' value={input.name} onChange={(e) => handleInputChange(e)}/>
        <label>Dificultad</label>
        <input className='dato' type="range" name="dificulty"  min="1" max="5" step="1" value={input.dificulty}
         onChange={(e) => handleInputChange(e)}/>
        <label>Duracion</label>
        <input className='dato' type='number' min='1' max='24' value={input.duration} onChange={(e) => handleInputChange(e)}/>
        <label>Temporada</label>      
        <input className='dato' type='text' name="season" value={input.season} onChange={(e) => handleInputChange(e)}/>
        <label>Paises en los que se puede realizar</label>       
        <input className='dato' type="text" name='countries'  onChange={(e) => handleInputChange(e)}/>
        </div>
      <input className='button' type="submit" onSubmit=''/>
    </form>
    </div>
  )
}


