import React from 'react';
import './Country.css';
import { Link } from 'react-router-dom';
import axios from 'axios';


export function Country () {
    let flag = "https://restcountries.eu/data/col.svg";
    let name = 'Colombia';
    let capital='Bogota';
    let continent='America del Sur'

    const getCountries = function () {
        let countryList; // En esta variable guardaremos todos los paises de la API externa.
        return axios.get("https://restcountries.eu/rest/v2/all")
          .then((response) => {
            countryList = response.data.map((c) => {
              return {
                flag: c.flag,
                name: c.name,
                continent: c.continent,
              };
            });
            return countryList;
        });
    }


    return (
        <div className='countryCard'>
            <Link to='/Country'>
            <img className='img' src= {flag} />
            </Link>
              <div className='list' >
              <p>Pais : </p>
              <p>{name}</p>
              </div>
              <div >
              <p>Continente : </p>
              <p>{continent}</p>
              </div>

        </div>

    )
};

export default Country;
