import React, { useEffect , useState} from 'react'
import { connect } from 'react-redux'
import { useSelector , useDispatch } from 'react-redux';
import { getAllActivities } from '../../Actions/actions';

export const Activities = () => {
    const activities = useSelector(state => state.activities)
    const dispatch = useDispatch()
    const [estado, setEstado] = useState({
        countries : [],
        activity:0,
        actividad:activities
    })

useEffect(() => {
   dispatch(getAllActivities());
}, [dispatch])

let actividad={};

const handleInputChange =  function(e) {
    e.preventDefault();
    setEstado({
      ...estado,
      [e.target.name]: e.target.value
    });
  }


const search = () => {
    setEstado({
      ...estado,
     actividad : activities[estado.activities]
    } )
}



    return (
        <div>
             <select name='activities'  onChange={handleInputChange} defaultValue='DEFAULT' >
					<option name='DEFAULT' >                                   
						Buscar por Actividad...                       
					</option>
          {
						activities &&                   
							activities.map( act => (								
								<option name={estado.countries} key={act.id} value={act.id}>                                   
									{act.name}                            
								</option>								        
							))
					}
				</select> 

                <ul>
                    {
                    estado.actividad &&
                          estado.actividad.map( act => (
                          <li>{act.Countries.name}
                          </li>
                          ))

                    }
                </ul><button onClick={search}>Go!</button>
        </div>
    )
}


const mapStateToProps = (state) => ({
    activities : state.activities
})

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Activities)
