import React , { useEffect, useState} from 'react';
import './countries.css';
import Country from '../Country/Country';
import { connect, useDispatch , useSelector } from 'react-redux';
import getCountries from '../../Actions/actions';



export const Countries = () => {
  const dispatch = useDispatch();
  const countries = useSelector(state => state.countries);



  let page = 5;
  let pageSize= 10;

  useEffect(() => {
    dispatch(getCountries(page , pageSize));
  }, [])


   return (

            <div  >
              <button onClick={() => page-1}>Anterior</button>
              <button onClick={() => page-1}>Siguiente</button>
                  <div className='cards'>
               {countries.map( pais => <Country
               key={pais.idCountry}
               idCountry={pais.idCountry}
               flag={pais.flag}
               name={pais.name}
               continent={pais.continent}
                />)}
                </div>
            </div>
          );   
}

const mapStateToProps = state => {
  return {
    countries : state.countries
  }
}

export default connect(mapStateToProps, null)(Countries) ;
