import React , {useEffect , useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './addactivities';
import { postActivity } from '../../Actions/actions';
import { connect } from 'react-redux';


export function  AddActivity(props) {
  const activities = useSelector(state => state.activities)
  const [input , setInput] = useState({
      name: '',
      dificulty: 1,
      duration:1,
      season:'',
      countries:[]
    })
  const dispatch = useDispatch();


   const handleInputChange = function(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  }

  function submit(data, e) {
    const alphaCode = [];
    const nombre = [];

    console.log(data);
    setInput({
      ...input,
      alphaCode: alphaCode,
      nombre: nombre,
      name: data.actividad,
      dificultad: data.dificultad,
      duracion: data.duracion,
      temporada: data.temporada,
    });

    dispatch(agregar(data));
    e.target.reset();
    reset({ data });
  }


  return (
    <div className='form'>
    <form >
      <div>
      <h1>Agregar Actividad Turistica</h1>
      <hr />
      </div>
        <div className='inputs'>
        <label>Nombre actividad</label>
        <input className='dato' type="text" name='name' value={input.name} onChange={(e) => handleInputChange(e)}/>
        <label>Dificultad</label>
        <input className='dato' type="range" name="dificulty"  min="1" max="5" step="1" value={input.dificulty}
         onChange={(e) => handleInputChange(e)}/>
        <label>Duracion</label>
        <input className='dato' type='number' name='duration' min='1' max='24' value={input.duration} onChange={(e) => handleInputChange(e)}/>
        <label>Temporada</label>      
        <input className='dato' type='text' name="season" value={input.season} onChange={(e) => handleInputChange(e)}/>
        <label>Paises en los que se puede realizar</label>       
        <input className='dato' type="text" name='countries'  onChange={(e) => handleInputChange(e)}/>
        </div>
      <input className='button' type="submit" onSubmit={submit}/>
    </form>
    </div>
  )
}






export default connect(null, postActivity())(AddActivity) ;

// Ruta de creación de actividad turística: debe contener

// [x] Un formulario controlado con los siguientes campos
// Nombre
// Dificultad
// Duración
// Temporada
// [ ] Posibilidad de seleccionar/agregar varios países en simultaneo
// [ ] Botón/Opción para crear una nueva actividad turística
