import React , { useEffect, useState} from 'react';
import './countries.css';
import Country from '../Country/Country';
import { connect, useDispatch , useSelector } from 'react-redux';
import getCountries from '../../Actions/actions';



export const Countries = () => {
  const dispatch = useDispatch();
  const countries = useSelector(state => state.countries);

  const [page ,setPage] = useState(0)



  // let page = 5;
  let pageSize= 10;

  useEffect(() => {
    dispatch(getCountries(page , pageSize));
  }, [page])

  function handlepagenext () {
    setPage(page +1)
  }
  function handlepageprev () {
    if(page===0) 
    setPage(page -1)
  }



  console.log(page)

   return (

            <div  >
              <button onClick={handlepageprev}>Anterior</button>
              <button onClick={handlepagenext}>Siguiente</button>
                  <div className='cards'>
               {countries.map( pais => <Country
               key={pais.idCountry}
               idCountry={pais.idCountry}
               flag={pais.flag}
               name={pais.name}
               continent={pais.continent}
                />)}
                </div>
            </div>
          );   
}

const mapStateToProps = state => {
  return {
    countries : state.countries
  }
}

export default connect(mapStateToProps, null)(Countries) ;
