import GET_COUNTRIES  from '../Actions/actions';

const initialState = {
    countries: [],
    countriesDetail: []
};

const Reducer = (state=initialState, action) => {
    switch (action.type) {
     case 'GET_COUNTRIES':
         return { 
             ...state,
             countries: action.payload,
          };
     case 'GET_DETAIL':
         return {
             ...state,
             countriesDetail: action.payload,
         }
    default :
    return state;
    }
}



export default Reducer;
