import { getCountries } from '../Actions/actions'
import { GET_COUNTRIES } from '../Actions/actions';

const initialState = {
    countries: [],
    countries
};

export function Reducer (state=initialState, action) {
    switch (action.type) {
     case 'getCountries':
         return [
             ...state,
             {...action.payload}
         ]
    default :
    return state;
    }
}



export default Reducer;
