import React , { useEffect, useState} from 'react';
import './countries.css';
import Country from '../Country/Country';
import { connect, useDispatch , useSelector } from 'react-redux';
import getCountries from '../../Actions/actions';


export const Countries = () => {
  const dispatch = useDispatch();
  const countries = useSelector(state => state.countries);


  useEffect(() => {
    dispatch(getCountries());
  }, []);

  console.log(countries)

  const showCountries = () => {
    countries.map(c => 
                  <Country 
                  name={c.name}
                  flag={c.flag}
                  continent={c.continent}
                  />
                )
  }


  {
    paises &&                   
        paises.map( (pais, indice) => (								
            indice <= paises.length/2 && <div key={pais.id} value={pais.id} className='imgBandera' >                                   
                <img src={pais.bandera} alt='ups...' style={{height: '2em', width: '3em'}} />                            
            </div>								        
        ))
}
  return (
            <div >
              <ul>
                  {countries.map( (country) => {
                    <li>{country.name}</li>
                  })
                  }  
              </ul>

            </div>
          );   
}

const mapStateToProps = state => {
  return {
    countries : state.countries
  }
}

export default connect(mapStateToProps, null)(Countries) ;
