import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css'
import logo from '../../img/logo.png'
import { getAllCountries } from '../../Actions/actions';


export function NavBar() {
  const countries = useSelector(state => state.allcountries); 

  useEffect(() => {
    getAllCountries()
  },[allcountries])


  return (
        <nav >
        <img src={logo} />
        <ul>
        <Link to='/'> <li><a className='dropbtn'> iWorld! </a> </li> </Link>
        <Link to='/home'> <li><a className='dropbtn'> Home </a></li> </Link>
        <Link to='/Activities'>
          <li className="dropdown"> 
           <a className='dropbtn'>Actividades </a>
           <div className="dropdown-content">
             <Link to='/addactivity'><a> Agregar Actividades </a></Link>
             <Link to='/listactivity'><a> Ver Actividades por Pais </a></Link>
           </div>
          </li>
        </Link>
        <Link to='/about'> <li><a className='dropbtn'> Sobre el Proyecto </a> </li> </Link>
        </ul>
    </nav>
    )
};

export default NavBar;
