import GET_COUNTRIES  from '../Actions/actions';

const initialState = {
    countries: [{name:arg} , {name:bra} , {name: chi}],
    countriesDetail: []
};

export function Reducer (state=initialState, action) {
    switch (action.type) {
     case 'GET_COUNTRIES':
         return { 
             ...state,
             countries: action.payload
          }
    default :
    return state;
    }
}



export default Reducer;
