import React , { useEffect} from 'react';
import './countries.css';
import Country from '../Country/Country';
import { connect, useDispatch } from 'react-redux';
import getCountries from '../../Actions/actions';



const Countries = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getCountries());
  }, []);

let countries = [{name:'arg'} , {name:'bra'} , {name: 'chi'}];

   return (
            <div >
            <ul className="paises_h">
        {paises.map((item, index) => (
          <div key={index} className="bloque_paises">
            <Link to="/home/detalle">
              <img
                className="img_h"
                src={item.bandera}
                alt="No se encuentra la imagen"
                onClick={() => dispatch(detalle(item.alphaCode))}
              />
            </Link>
            <h3>{item.nombre}</h3>
            <h4>{item.continente}</h4>
          </div>
        ))}
      </ul>        
            </div>
          );   
}

const mapStateToProps = state => {
  return {
    props : state.countries
  }
}

export default connect(mapStateToProps, null)(Countries) ;
